frappe.ui.form.on('Inward', {
    onload: function(frm) {
        if (frm.is_new()) {
            // add_default_bag_types(frm);
            if (!frm.doc.report_type) {
            frm.set_value('report_type', 'Multi Report');
            }

            load_items_by_report_type(frm);

            // Load default items based on current report type after form loads
            // setTimeout(() => {
            //   
            // }, 500);

            // Wait for child table to load properly
            setTimeout(function() {
                calculate_bag_count(frm, function() {
                    setTimeout(function() {
                        auto_populate_deductions(frm);
                    }, 100);
                });
            }, 200);
        }

        load_bag_type_options(frm);
    },

    refresh: function(frm) {
        // Calculate all totals when form loads
        calculate_all_totals(frm);
        if(frm.doc.customer) {
            frappe.db.get_value('Customer', frm.doc.customer, 'customer_type')
            .then(r => {
                if(r.message.customer_type === 'Company / Trader') {
                    frm.set_df_property('vendor_gstin', 'hidden', 0);
                }
                else if(customer_type === 'Individual / Farmers')
                {
                    frm.set_df_property('vendor_gstin', 'hidden', 1);
                }

            });
        }


        // if (frm.doc.report_type) {
        //     load_items_by_report_type(frm);
        // }

    },

    // ===========================================
    // SAUDA SELECTION - AUTO POPULATE
    // ===========================================
    sauda: function(frm) {
        if (frm.doc.sauda) {
