// ===============================================
// CALCULATION FUNCTIONS
// ===============================================

function calculate_item_amount(frm, cdt, cdn) {
    // Calculate item_amount for individual row
    let row = locals[cdt][cdn];

    if (row.item_arrival_weight >= 0 && row.item_rate >= 0) {
        // KG to Quintal conversion: item_amount = (arrival_weight / 100) * rate
        row.item_amount = (row.item_arrival_weight / 100) * row.item_rate;
        frm.refresh_field('inward_items');
    }
}

function calculate_item_totals(frm) {
    // Calculate parent totals from inward_items child table
    let total_gross_weight = 0;
    let total_bags = 0;
    let total_arrival_weight = 0;
    let total_amount = 0;

    if (frm.doc.inward_items) {
        frm.doc.inward_items.forEach(function(item) {
            if (item.item_gross_weight) total_gross_weight += item.item_gross_weight;
            if (item.item_bags) total_bags += item.item_bags;
            if (item.item_arrival_weight) total_arrival_weight += item.item_arrival_weight;
            if (item.item_amount) total_amount += item.item_amount;
        });
    }

    frm.set_value('total_gross_weight', total_gross_weight);
    frm.set_value('total_bags', total_bags);
    frm.set_value('total_arrival_weight', total_arrival_weight);
    frm.set_value('total_amount', total_amount);

    // Trigger dependent calculations
    calculate_sub_total(frm);
}

function calculate_deduction_totals(frm) {
    // Calculate total_deductions from deductions child table
    let total_deductions = 0;

    if (frm.doc.deductions) {
        frm.doc.deductions.forEach(function(deduction) {
            if (deduction.deduction_amount) {
                total_deductions += deduction.deduction_amount;
            }
        });
    }

    frm.set_value('total_deductions', total_deductions);

    // Trigger dependent calculations
    calculate_sub_total(frm);
}

function calculate_sub_total(frm) {
    // Calculate sub_total = total_amount - total_deductions
    let sub_total = (frm.doc.total_amount || 0) - (frm.doc.total_deductions || 0);
    frm.set_value('sub_total', sub_total);

    // Trigger tax calculations
    calculate_taxes(frm);
}

function calculate_taxes(frm) {
    // Calculate all tax amounts based on sub_total
    let sub_total = frm.doc.sub_total || 0;
    let total_gross_weight = frm.doc.total_gross_weight || 0;

    // GST Calculation
    let cgst_percent = frm.doc.cgst_percent || 0;
    let sgst_percent = frm.doc.sgst_percent || 0;
    let igst_percent = frm.doc.igst_percent || 0;
    let total_gst_amount = sub_total * (cgst_percent + sgst_percent + igst_percent) / 100;

    // TCS Calculation
    let tcs_percent = frm.doc.tcs_percent || 0;
    let tcs_amount = sub_total * tcs_percent / 100;

    // TDS Calculation
    let tds_percent = frm.doc.tds_percent || 0;
    let tds_amount = sub_total * tds_percent / 100;

    let cgst_amount = sub_total * cgst_percent / 100;

    let sgst_amount = sub_total * sgst_percent / 100;

    let igst_amount = sub_total * igst_percent / 100;

    // Broker Commission Calculation (for records only)
    let broker_commission_percent = frm.doc.broker_commission_percent || 0;
    let broker_commission_amount = total_gross_weight * broker_commission_percent / 100;

    // Set calculated values
    frm.set_value('total_gst_amount', total_gst_amount);
    frm.set_value('tcs_amount', tcs_amount);
    frm.set_value('tds_amount', tds_amount);
    frm.set_value('broker_commission_amount', broker_commission_amount);
    frm.set_value('cgst_amount', cgst_amount);
    frm.set_value('sgst_amount', sgst_amount);
    frm.set_value('igst_amount', igst_amount);

    // Calculate final net_total
    calculate_net_total(frm);
}

function calculate_net_total(frm) {
    // Calculate net_total = sub_total + total_gst_amount + tcs_amount - tds_amount
    let sub_total = frm.doc.sub_total || 0;
    let total_gst_amount = frm.doc.total_gst_amount || 0;
    let tcs_amount = frm.doc.tcs_amount || 0;
    let tds_amount = frm.doc.tds_amount || 0;

    let net_total = sub_total + total_gst_amount + tcs_amount - tds_amount;
    frm.set_value('net_total', net_total);

    // Trigger payment calculations
    calculate_payment_totals(frm);
}

function calculate_payment_totals(frm) {
    // Calculate payment summary from inward_payments child table
    let total_amount_paid = 0;
    let last_payment_date = null;
    let has_pending_payments = false;
    let has_completed_payments = false;

    if (frm.doc.inward_payments) {
        frm.doc.inward_payments.forEach(function(payment) {
            // Only count success payments in total_amount_paid
            if (payment.payment_status === 'success' && payment.payment_amount) {
                total_amount_paid += payment.payment_amount;
                has_completed_payments = true;

                // Track latest payment date
                if (payment.payment_date) {
                    if (!last_payment_date || payment.payment_date > last_payment_date) {
                        last_payment_date = payment.payment_date;
                    }
                }
            }

            // Check for pending payments
            if (payment.payment_status === 'pending') {
                has_pending_payments = true;
            }
        });
    }

    // Calculate pending amount
    let net_total = frm.doc.net_total || 0;
    let total_amount_pending = net_total - total_amount_paid;

    // Determine overall payment status using new status names
    let inward_payment_status = 'pending';
    if (total_amount_paid >= net_total) {
        inward_payment_status = 'success';
    } else if (total_amount_paid > 0) {
        inward_payment_status = 'processing';
    }

    // Set calculated values
    frm.set_value('total_amount_paid', total_amount_paid);
    frm.set_value('total_amount_pending', Math.max(0, total_amount_pending));
    frm.set_value('inward_payment_status', inward_payment_status);
    frm.set_value('last_payment_date', last_payment_date);
}

function calculate_all_totals(frm) {
    // Master function to recalculate everything
    calculate_item_totals(frm);
    calculate_deduction_totals(frm);
    calculate_payment_totals(frm);
}


function calculate_all_bags_weights(frm, cdt, cdn) {
    let row = locals[cdt][cdn];

    // Calculate Average weight
    let average_weight_per_bag = frm.doc.gross_weight / frm.doc.inward_total_bags;

    // Calculate total bag weight for each bag
    let deduct_bag_weight = row.item_bags * row.item_charges;

    // Get current value
    let current_value = frappe.model.get_value(cdt, cdn, 'item_gross_weight');


    let item_gross_weight = row.item_bags * average_weight_per_bag;


    // Handle NaN and comparison properly
    let should_update = false;

    if (isNaN(current_value) && !isNaN(item_gross_weight)) {
        // Current is NaN but new value is valid number
        should_update = true;
    } else if (!isNaN(current_value) && !isNaN(item_gross_weight) && current_value !== item_gross_weight) {
        // Both are valid numbers but different
        should_update = true;
    } else if (isNaN(current_value) && isNaN(item_gross_weight)) {
        // Both are NaN, no need to update
        should_update = false;
    }

    // Only set if value should be updated
    if (should_update) {
        frappe.model.set_value(cdt, cdn, 'item_gross_weight', item_gross_weight);
    }

    frappe.model.set_value(cdt, cdn, 'item_deduct_weight', deduct_bag_weight);

    // Calculate arrival weight
    // Arrival Weight = (No of bags × Average weight per bag) - Deduct weight
    if (row.item_bags || row.item_gross_weight) {
        let item_arrival_weight = (row.item_bags * average_weight_per_bag) - deduct_bag_weight;
        frappe.model.set_value(cdt, cdn, 'item_arrival_weight', item_arrival_weight);
    }
    frm.refresh_field('items');
}

function calculate_multi_rate_amount(frm, cdt, cdn) {
    // Only calculate for Multi Rate Report
    if (frm.doc.report_type !== 'Multi Rate') {
        return;
    }

    let row = locals[cdt][cdn];


    // Calculate Average weight
    let average_weight_per_bag = frm.doc.gross_weight/frm.doc.inward_total_bags;

    // Calculate No of Bags
    let no_of_bags = parseInt(row.item_gross_weight / average_weight_per_bag);
    frappe.model.set_value(cdt, cdn, 'item_bags', no_of_bags);

    // ========================================
    // 1. CALCULATE DEDUCT WEIGHT (when bags or charges change)
    // ========================================
    if (row.item_bags && row.item_charges && 
        row.item_bags > 0 && row.item_charges > 0) {

        let deduct_bag_weight = row.item_bags * row.item_charges;

        // Round to 2 decimal places
        deduct_bag_weight = Math.round(deduct_bag_weight * 100) / 100;

        frappe.model.set_value(cdt, cdn, 'item_deduct_weight', deduct_bag_weight);

        // After calculating deduct weight, recalculate arrival weight
        if (row.item_gross_weight && row.item_gross_weight > 0) {
            let calculated_arrival_weight = row.item_gross_weight - deduct_bag_weight;
            calculated_arrival_weight = Math.round(calculated_arrival_weight * 100) / 100;

            frappe.model.set_value(cdt, cdn, 'item_arrival_weight', calculated_arrival_weight);
        }

    } else if (row.item_bags === 0 || row.item_charges === 0) {
        // Clear deduct weight if bags or charges are 0
        frappe.model.set_value(cdt, cdn, 'item_deduct_weight', 0);

        // Recalculate arrival weight when deduct weight becomes 0
        if (row.item_gross_weight && row.item_gross_weight > 0) {
            frappe.model.set_value(cdt, cdn, 'item_arrival_weight', row.item_gross_weight);
        }
    }

    // ========================================
    // 2. CALCULATE GROSS WEIGHT (when arrival weight changes)
    // Formula: gross_weight = arrival_weight + deduct_weight
    // ========================================
    // if (row.item_arrival_weight && row.item_arrival_weight > 0) {
    //     let current_deduct_weight = row.item_deduct_weight || 0;
    //     let calculated_gross_weight = row.item_arrival_weight + current_deduct_weight;

    //     // Round to 2 decimal places
    //     calculated_gross_weight = Math.round(calculated_gross_weight * 100) / 100;

    //     frappe.model.set_value(cdt, cdn, 'item_gross_weight', calculated_gross_weight);
    // } else {
    //     // Clear gross weight if arrival weight is not valid
    //     frappe.model.set_value(cdt, cdn, 'item_gross_weight', 0);
    // }

    // ========================================
    // 3. CALCULATE AMOUNT (based on arrival weight and rate)
    // Formula: amount = arrival_weight × rate
    // ========================================
    if (row.item_arrival_weight && row.item_rate && 
        row.item_arrival_weight > 0 && row.item_rate > 0) {

        // Calculate amount = arrival_weight × rate
        let calculated_amount = (row.item_arrival_weight * row.item_rate) / 100;

        // Round to 2 decimal places
        // calculated_amount = Math.round(calculated_amount * 100) / 100;

        // Set the calculated amount
        frappe.model.set_value(cdt, cdn, 'item_amount', calculated_amount);

    } else {

        // Clear amount if values are incomplete
        if (row.item_amount) {
            frappe.model.set_value(cdt, cdn, 'item_amount', 0);
        }

    }

    // 4. HANDLE SECOND ROW GROSS WEIGHT (Multi Rate Report)
    // Update second row when first row arrival weight changes
    // ========================================
    if (row.idx === 1 && row.item_gross_weight && frm.doc.inward_items.length >= 2) {
        // Call the existing calculate_auto_gross_weight function for second row
        let second_row = frm.doc.inward_items[1];

        setTimeout(() => {
            calculate_auto_gross_weight(frm, second_row.doctype, second_row.name);
        }, 300);

    }

    // Refresh the child table to show all updates
    frm.refresh_field('inward_items');
}

