function auto_populate_deductions(frm, add_only_for_new_item = false) {
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'App Settings'
        },
        callback: function(r) {
            if (r.message && r.message.default_deduction_types) {
                let deduction_types = r.message.default_deduction_types;
                let bag_type_count;

                if (frm.doc.report_type === 'Multi Rate') {
                    bag_type_count = 1;
                } else {
                    bag_type_count = frm.doc.inward_items.length;
                }
                let has_plastic = has_plastic_bag_type(frm);

                if (frm.doc.report_type !== 'Multi Rate' && add_only_for_new_item) {
                    // Rebuild table with proper order
                    let existing_deductions = [...frm.doc.deductions];
                    let new_item_index = bag_type_count - 1;

                    // Clear table first
                    frm.clear_table('deductions');

                    // Filter multiple deductions
                    let multiple_deductions = deduction_types.filter(function(deduction_type) {
                        return deduction_type.deduction_category === 'multiple';
                    });

                    // Rebuild table with proper mapping
                    multiple_deductions.forEach(function(deduction_type, type_index) {
                        for (let item_index = 0; item_index < bag_type_count; item_index++) {
                            let row = frm.add_child('deductions');
                            row.deduction_type = deduction_type.deduction_name;
                            row.required_value = deduction_type.required_value;

                            if (deduction_type.deduction_name === 'Damage' || deduction_type.deduction_name === 'UNLOADING' || deduction_type.deduction_name === 'Others') {
                                row.charges_per_unit = deduction_type.charges_per_unit;
                            }

                            // Copy existing data if this is not the new item
                            if (item_index !== new_item_index) {
                                let old_row = existing_deductions.find(function(old_row) {
                                    return old_row.deduction_type === deduction_type.deduction_name && 
                                           (old_row.idx - 1) % (bag_type_count - 1) === (item_index > new_item_index ? item_index - 1 : item_index);
                                });

                                if (old_row) {
                                    row.bags = old_row.bags;
                                    row.charges = old_row.charges;
                                    row.required_value = old_row.required_value;
                                    row.actual_value = old_row.actual_value;
                                    row.difference_value = old_row.difference_value;
                                    row.deduction_amount = old_row.deduction_amount;
                                }
                            }
                        }
                    });

                    // Add single deductions
                    deduction_types.forEach(function(deduction_type) {
                        if (deduction_type.deduction_category === 'single') {
                            if (deduction_type.deduction_name === 'PP' && !has_plastic_bag_type(frm)) {
                                return;
                            }
                            let old_row = existing_deductions.find(function(old_row) {
                                return old_row.deduction_type === deduction_type.deduction_name;
                            });

                            let row = frm.add_child('deductions');
                            row.deduction_type = deduction_type.deduction_name;

                            if (old_row) {
                                row.bags = old_row.bags;
                                row.charges = old_row.charges;
                                row.required_value = old_row.required_value;
                                row.actual_value = old_row.actual_value;
                                row.difference_value = old_row.difference_value;
                                row.deduction_amount = old_row.deduction_amount;
                            }
                        }
                    });

                } else {
                    // Clear and repopulate everything

                    if (add_only_for_new_item) {
                        return ;
                    }
                    frm.clear_table('deductions');
                    deduction_types.forEach(function(deduction_type) {
                        if (deduction_type.deduction_category === 'multiple') {
                            for (let i = 0; i < bag_type_count; i++) {
                                let row = frm.add_child('deductions');
                                row.deduction_type = deduction_type.deduction_name;
                                row.required_value = deduction_type.required_value;
                                row.deduction_category = deduction_type.deduction_category;
                                if (deduction_type.deduction_name === 'Damage') {
                                    row.charges_per_unit = deduction_type.charges_per_unit;
                                }
                            }
                        } else if (deduction_type.deduction_category === 'single') {
                            if (deduction_type.deduction_name === 'PP' && !has_plastic_bag_type(frm)) {
                                return;
                            }
                            let row = frm.add_child('deductions');
                            row.deduction_type = deduction_type.deduction_name;
                            row.deduction_category = deduction_type.deduction_category;
                            if(deduction_type.deduction_name === 'UNLOADING')
                            {
                                row.charges_per_unit = deduction_type.charges_per_unit;

                                if(frm.doc.gross_weight) 
                                {
                                    row.deduction_amount = (frm.doc.gross_weight * deduction_type.charges_per_unit) / 100;
                                }
                            }
                            else if (deduction_type.deduction_name === 'Others')
                            {
                                row.charges_per_unit = deduction_type.charges_per_unit;

                                if(frm.doc.total_amount) 
                                {
                                    row.deduction_amount = (frm.doc.total_amount * deduction_type.charges_per_unit) / 100;
                                }
                            }
                            else if(deduction_type.deduction_name === 'PP' && frm.doc.report_type === 'Single Report')
                            {
                                row.charges_per_unit = deduction_type.charges_per_unit;

                                if(frm.doc.gross_weight) 
                                {
                                    row.deduction_amount = (frm.doc.gross_weight * deduction_type.charges_per_unit) / 100;
                                }
                            }
                            else
                            {
                                row.charges_per_unit = deduction_type.charges_per_unit;
                                row.deduction_amount = deduction_type.charges_per_unit;
                            }

                        }
                    });
                }

                frm.refresh_field('deductions');
            }
        }
    });
}

function has_plastic_bag_type(frm) {
    return frm.doc.inward_items && frm.doc.inward_items.some(function(item) {
        return item.item_bag_type && item.item_bag_type.toLowerCase() === 'plastic';
    });
}

function calculate_deduction_amount(frm, cdt, cdn) {
    let deduction_row = locals[cdt][cdn];
    let deduction_idx = deduction_row.idx - 1; // Convert to 0-based index

    // Get total item details count
    let item_count = frm.doc.inward_items.length;

    if (item_count === 0) return;

    let required = deduction_row.required_value || 0;
    let actual = deduction_row.actual_value || 0;
    let difference = actual - required;
    if(difference <= 0){
        difference = 0;
    }

    // Get charges from deduction table itself
    let charges = deduction_row.charges_per_unit || 0;

        // Get deduction type
    let deduction_type = deduction_row.deduction_type || '';
    let amount = 0;


    if (deduction_type.toLowerCase() !== 'moise') {
        // Set the difference
        frappe.model.set_value(cdt, cdn, 'difference_value', difference);
    }

    // frappe.model.set_value(cdt, cdn, 'difference_value', difference);


    // ========================================
    // MULTI RATE REPORT vs OTHER REPORTS LOGIC
    // ========================================

    if (frm.doc.report_type === 'Multi Rate') {

        // ========================================
        // MULTI RATE: Use TOTAL values from form fields
        // ========================================

        let total_amount = frm.doc.total_amount || 0;
        let total_arrival_weight = frm.doc.total_arrival_weight || 0;
        let total_gross_weight = frm.doc.total_gross_weight || 0;

        // Calculate amount based on deduction type using TOTALS
        if (deduction_type.toLowerCase() === 'moise') {
            // Check if tiered calculation is enabled for Moise
            check_moise_tiered_calculation(actual, required, total_amount, function(calculated_amount) {
                frappe.model.set_value(cdt, cdn, 'deduction_amount', calculated_amount);
            },cdt,cdn);
            return; // Exit early for Moise as it's async

        } else if (deduction_type.toLowerCase() === 'damage') {
            // Damage = (diff * charges * total_arrival_weight) / 100
            amount = (difference * charges * total_gross_weight) / 100;

        } else if (deduction_type.toLowerCase() === 's/s') {
            // S/S = (diff * total_amount) / 100
            amount = (difference * total_amount) / 100;
        }

    } else {

        // ========================================
        // MULTI STATE & SINGLE REPORT: Use individual item values (original logic)
        // ========================================

        // Calculate which item this deduction maps to
        let item_index = deduction_idx % item_count;
        let corresponding_item = frm.doc.inward_items[item_index];
        if (!corresponding_item) return;
        let item_gross_weight = corresponding_item.item_gross_weight || 0;
        let rate = corresponding_item.item_rate || 0;
        // let item_amount = corresponding_item.item_amount || 0;
        let item_amount = ((item_gross_weight - corresponding_item.item_deduct_weight) * rate) / 100;

        // Calculate amount based on deduction type using INDIVIDUAL values
        if (deduction_type.toLowerCase() === 'moise') {
            // Check if tiered calculation is enabled for Moise
            check_moise_tiered_calculation(actual, required, item_amount, function(calculated_amount) {
                frappe.model.set_value(cdt, cdn, 'deduction_amount', calculated_amount);
            },cdt,cdn);
            return; // Exit early for Moise as it's async

        } else if (deduction_type.toLowerCase() === 'damage') {
            // Damage = (diff * charges * item_gross_weight) / 100
            amount = (difference * charges * item_gross_weight) / 100;

        } else if (deduction_type.toLowerCase() === 's/s') {
            // S/S = (diff * item_amount) / 100
            amount = (difference * item_amount) / 100;
        }
    }

    // Set the calculated amount (for non-Moise deductions)
    frappe.model.set_value(cdt, cdn, 'deduction_amount', amount);
}

// function to update deduction bags
function update_deduction_bags_for_item(frm, cdt, cdn) {

  let item_row = locals[cdt][cdn];
  let item_idx = item_row.idx - 1; // Convert to 0-based index
  let new_bags = item_row.item_bags || 0;

  // Get total item count
  let item_count = frm.doc.inward_items.length;

    // Update all deduction rows that map to this item
    frm.doc.deductions.forEach(function(deduction_row, deduction_idx) {
        if (deduction_row.deduction_category === 'multiple') {
            // Check if this deduction row maps to the changed item
            let mapped_item_index = deduction_idx % item_count;
            if (mapped_item_index === item_idx) {
                // This deduction row corresponds to the changed item
                frappe.model.set_value(
                    'Inward Deduction',
                    deduction_row.name,
                    'bags',
                    new_bags
                );
            }
        }
    });

  frm.refresh_field('Inward Deduction');
}

function remove_mapped_deduction_rows(frm, deleted_item_index) {
    let item_count = frm.doc.inward_items.length + 1; // +1 because item was just deleted
    let rows_to_remove = [];

    // Find all deduction rows that were mapped to the deleted item
    frm.doc.deductions.forEach(function(deduction_row, deduction_idx) {
        let mapped_item_index = deduction_idx % item_count;

        // Delete only if mapped AND NOT "multiple"
        if (mapped_item_index === deleted_item_index && deduction_row.deduction_category !== 'single') {
            rows_to_remove.push(deduction_row.name);
        }
    });

    // Remove rows in reverse order
    for (let i = frm.doc.deductions.length - 1; i >= 0; i--) {
        if (rows_to_remove.includes(frm.doc.deductions[i].name)) {
            frm.doc.deductions.splice(i, 1);
        }
    }

    // Re-index all remaining rows to reset No. column
    frm.doc.deductions.forEach(function(row, index) {
        row.idx = index + 1;
    });

    frm.refresh_field('deductions');
}

function check_moise_tiered_calculation(actual, required, item_amount, callback,cdt,cdn) {
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'App Settings'
        },
        callback: function(r) {
            if (r.message && r.message.default_deduction_types) {
                // Find Moise deduction configuration
                let moise_config = r.message.default_deduction_types.find(function(deduction) {
                    return deduction.deduction_name.toLowerCase() === 'moise';
                });

                if (moise_config && moise_config.has_tiered_calculation) {
                     // Get tier ranges from App Settings child table
                    let tier_ranges = r.message.deduction_tier_range || []; // Child table data
                    calculate_moise_tiered_amount(actual, required, item_amount, tier_ranges, function(amount, difference) {
                        // Set the difference value first
                        frappe.model.set_value(cdt, cdn, 'difference_value', difference);
                        // Then return the amount through original callback
                        callback(amount);
                    });
                } else {
                    // Use regular calculation
                    let difference = actual - required;
                    if(difference <= 0){
                        difference = 0;
                    }
                    frappe.model.set_value(cdt, cdn, 'difference_value', difference);
                    let amount = (difference * item_amount) / 100;
                    callback(amount);
                }
            } else {
                // Fallback to regular calculation
                let difference = actual - required;
                if(difference <= 0){
                    difference = 0;
                }
                frappe.model.set_value(cdt, cdn, 'difference_value', difference);
                let amount = (difference * item_amount) / 100;
                callback(amount);
            }
        }
    });
}

function calculate_moise_tiered_amount(actual, required, item_amount, tier_ranges, callback) {

    // If actual <= required, no charges apply
    if (actual <= required) {
        callback(0, 0);
        return;
    }

    if (!tier_ranges || tier_ranges.length === 0) {
        let difference = actual - required;
        if(difference <= 0){
            difference = 0;
        }
        let amount = (difference * item_amount) / 100;
        callback(amount, difference); // callback(amount, difference)
        return;
    }

    let total_weighted_diff = 0;

    // Sort tier ranges by range_from to ensure proper order
    let sorted_ranges = tier_ranges.sort(function(a, b) {
        return a.range_from - b.range_from;
    });


    for (let i = 0; i < sorted_ranges.length; i++) {
        let tier = sorted_ranges[i];
        let tier_start = tier.range_from;
        let tier_end = tier.range_to;

        // Skip if actual value hasn't reached this tier
        if (actual < tier_start) {
            break;
        }

        // Calculate the portion that falls in this tier
        let range_start, range_end;

        if (i === 0) {
            // First tier: start from (required + 1)
            range_start = required + 1;
        } else {
            // Other tiers: start from tier_start
            range_start = tier_start;
        }

        // End is minimum of tier_end and actual
        range_end = Math.min(tier_end, actual);

        if (range_end >= range_start) {
            let tier_diff = range_end - range_start + 1; // +1 for inclusive counting
            let weighted_diff = tier_diff * tier.multiplier;
            total_weighted_diff += weighted_diff;
        }
    }

    let final_amount = (total_weighted_diff * item_amount) / 100;

    callback(final_amount, total_weighted_diff);
}

function load_bag_type_options(frm) {
