    });
}

function validate_bag_count(frm) {
    let total_bags = 0;

    (frm.doc.inward_items || []).forEach(row => {
        total_bags += row.item_bags || 0;
    });

    if (total_bags > (frm.doc.inward_total_bags || 0)) {
        frappe.msgprint({
            title: __('Bag Count Exceeded'),
            message: __('Sum of item bags cannot be greater than Total Bags'),
            indicator: 'red'
        });
    }
}

function calculate_and_update_debit_note(frm) {
    // Get the required values
    let vendor_amount = frm.doc.vendor_amount || 0;
    let rate_per_quintal = frm.doc.rate_per_quintal || 0;
    let vendor_weight = frm.doc.vendor_weight || 0;
    let total_arrival_weight = frm.doc.total_arrival_weight || 0;
    let total_deductions = frm.doc.total_deductions || 0;

    // Calculate the amount and deducted weight
    let deducted_weight_kg = 0;

    // Calculate deducted weight only if rate_per_quintal is not zero
    if (vendor_weight && total_arrival_weight && total_arrival_weight > 0) {
        deducted_weight_kg = vendor_weight - total_arrival_weight;
    }

    if(deducted_weight_kg > 0)
    {
        var amount = (deducted_weight_kg * rate_per_quintal) / 100;
    }

    // ========================================
    // FIRST ROW: Weight Shortage
    // ========================================
    if (frm.doc.debit_note && frm.doc.debit_note.length > 0) {
        // Update the first row
        let row = frm.doc.debit_note[0];
        row.amount = amount;
        row.deducted_weight_kg = deducted_weight_kg;

        // Set default particulars if not already set
        if (!row.particulars) {
            row.particulars = "Weight Shortage";
        }

        frm.refresh_field('debit_note');
    } else {
        // Add a new row to the child table
        let child = frm.add_child('debit_note');
        child.particulars = "Weight Shortage";
        child.amount = amount;
        child.deducted_weight_kg = deducted_weight_kg;

        frm.refresh_field('debit_note');
    }

    // ========================================
    // SECOND ROW: Quality and other deductions
    // ========================================
    if (frm.doc.debit_note && frm.doc.debit_note.length > 1) {
        // Update the second row
        let row = frm.doc.debit_note[1];
        row.amount = total_deductions;

        // Set default particulars if not already set
        if (!row.particulars) {
            row.particulars = "Quality and other deductions";
        }

        frm.refresh_field('debit_note');
    } else {
        // Add a new second row to the child table
        let child = frm.add_child('debit_note');
        child.particulars = "Quality and other deductions";
        child.amount = total_deductions;

        frm.refresh_field('debit_note');
    }

    // Refresh the form to show updated values
    frm.refresh();
}

function update_unloading_deduction(frm) {
    if (!frm.doc.gross_weight) {
        return;
    }

    // Find the row with deduction_type = 'UNLOADING'
    let unloading_row = null;

    frm.doc.deductions.forEach(function(row) {
        if (row.deduction_type === 'UNLOADING') {
            unloading_row = row;
            return false; // break the loop
        }
    });


    if (unloading_row) {
        // Calculate new amount: (gross_weight * charges_per_unit) / 100
        let new_amount = (frm.doc.gross_weight * unloading_row.charges_per_unit) / 100;

        // Update the amount field
        frappe.model.set_value(unloading_row.doctype, unloading_row.name, 'deduction_amount', new_amount);

        // Refresh the child table
        frm.refresh_field('deduction');
    }
}

function update_others_deduction(frm) {
    if (!frm.doc.total_amount) {
        return;
    }

    // Find the row with deduction_type = 'Others'
    let others_row = null;
    frm.doc.deductions.forEach(function(row) {
        if (row.deduction_type === 'Others') {
