}

function load_items_by_report_type(frm) {
    let report_type = frm.doc.report_type;

    if (!report_type) {
        return;
    }

    // Fetch App Settings with default bag types
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'App Settings',
            name: 'App Settings'
        },
        callback: function(response) {
            if (response.message && response.message.default_bag_types) {
                let app_settings = response.message;
                let bag_types = app_settings.default_bag_types;

                // Clear existing items
                frm.clear_table('inward_items');

                // Load items based on report type
                let loaded_count = 0;

                switch (report_type) {
                    case 'Single Report':
                        loaded_count = load_single_report_items(frm, bag_types);
                        calculate_bag_count(frm);
                        auto_populate_deductions(frm);
                        break;
                    case 'Multi Report':
                        loaded_count = load_multi_state_report_items(frm, bag_types);
                        calculate_bag_count(frm);
                        auto_populate_deductions(frm);
                        break;
                    case 'Multi Rate':
                        loaded_count = load_multi_rate_report_items(frm, bag_types);
                        calculate_bag_count(frm);
                        auto_populate_deductions(frm);
                        break;
                    default:
                        frappe.show_alert({
                            message: 'Invalid report type selected',
                            indicator: 'red'
                        });
                        return;
                }

                // Refresh child table to show new items
                frm.refresh_field('inward_items');

            } else {
                frappe.show_alert({
                    message: 'No default bag types found in App Settings',
                    indicator: 'orange'
                });
            }
        },
        error: function(error) {
            frappe.show_alert({
                message: 'Failed to load App Settings',
                indicator: 'red'
            });
        }
    });
}

function load_single_report_items(frm, bag_types) {
    if (bag_types && bag_types.length > 0) {
        // Load only FIRST record from App Settings default bag types
        let first_item = bag_types[0];
        let child_row = frm.add_child('inward_items');

        child_row.item_bag_type = first_item.bag_type;
        child_row.item_charges = first_item.charges;

        if (frm.doc.rate_per_quintal) {
            child_row.item_rate = frm.doc.rate_per_quintal;
        }

        if (frm.doc.inward_total_bags) {
            child_row.item_bags = frm.doc.inward_total_bags;
        }

        if (frm.doc.inward_total_bags && child_row.item_charges) {
            child_row.item_deduct_weight = child_row.item_charges * child_row.item_bags;
        }

        if (frm.doc.gross_weight) {
            child_row.item_gross_weight = frm.doc.gross_weight;
