function calculate_auto_gross_weight(frm, cdt, cdn) {
    if (frm.doc.report_type !== 'Multi Rate') return;

    let current_row = locals[cdt][cdn];
    let parent_gross_weight = frm.doc.gross_weight || 0;

    if (parent_gross_weight <= 0) {
        return;
    }

    // Calculate sum of all OTHER rows' gross weight (excluding current row)
    let sum_other_gross_weights = 0;

    if (frm.doc.inward_items && frm.doc.inward_items.length > 0) {
        frm.doc.inward_items.forEach(function(item_row) {
            // Skip current row and only sum rows that have gross weight
            if (item_row.name !== current_row.name && item_row.item_gross_weight) {
                sum_other_gross_weights += item_row.item_gross_weight;
            }
        });
    }

    // Calculate remaining gross weight
    let calculated_gross_weight = parent_gross_weight - sum_other_gross_weights;

    // Ensure it's not negative
    if (calculated_gross_weight < 0) {
        calculated_gross_weight = 0;
    }

    // Round to 2 decimal places
    calculated_gross_weight = Math.round(calculated_gross_weight * 100) / 100;

    // Set the calculated arrival weight
    frappe.model.set_value(cdt, cdn, 'item_gross_weight', calculated_gross_weight);


    // Trigger multi rate calculation for this row
    setTimeout(() => {
        calculate_multi_rate_amount(frm, cdt, cdn);
    }, 200);
}

