    }

    // Check if PP deduction already exists
    let pp_exists = false;
    if (frm.doc.deductions) {
        pp_exists = frm.doc.deductions.some(function(row) {
            return row.deduction_type === 'PP';
        });
    }

    // If PP doesn't exist, add it
    if (!pp_exists) {
