function remove_pp_deduction(frm) {
    if (!frm.doc.deductions) return;

    // Find and remove PP deduction rows (reverse loop to avoid index issues)
    for (let i = frm.doc.deductions.length - 1; i >= 0; i--) {
        if (frm.doc.deductions[i].deduction_type === 'PP') {
            frm.doc.deductions.splice(i, 1);
        }
    }

    // Refresh the deductions table
    frm.refresh_field('deductions');
}

function add_pp_deduction_from_settings(frm) {
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'App Settings'
        },
        callback: function(r) {
            if (r.message && r.message.default_deduction_types) {
                let pp_deduction_type = r.message.default_deduction_types.find(function(deduction_type) {
                    return deduction_type.deduction_name === 'PP';
                });

                if (pp_deduction_type) {
                    let row = frm.add_child('deductions');
                    row.deduction_type = pp_deduction_type.deduction_name;
                    row.deduction_category = pp_deduction_type.deduction_category;
                    row.charges_per_unit = pp_deduction_type.charges_per_unit;
                    row.deduction_amount = pp_deduction_type.charges_per_unit;

                    frm.refresh_field('deductions');

                    // Set read-only property
                    setTimeout(() => {
                        let grid_row = frm.fields_dict.deductions.grid.grid_rows_by_docname[row.name];
                        if (grid_row) {
                            let deduction_amount_field = grid_row.docfields.find(field => field.fieldname === 'deduction_amount');
                            if (deduction_amount_field) {
                                deduction_amount_field.read_only = pp_deduction_type.deduction_category !== 'miscellaneous' ? 1 : 0;
                            }
                        }
                    }, 100);
                }
            }
        }
    });
}

// Function to update PP deduction amount
function update_pp_deduction(frm) {
    if (!frm.doc.deductions) return;

    // Find PP deduction row
    let pp_row = null;
    frm.doc.deductions.forEach(function(row) {
        if (row.deduction_type === 'PP') {
            pp_row = row;
            return false; // break the loop
        }
    });

    if (pp_row) {
        // Calculate plastic gross weight
        let plastic_weight_kg = calculate_plastic_gross_weight(frm);
        // Calculate new amount: (plastic_weight_kg * charges_per_unit) / 100
        // Since charges are per quintal and 1 quintal = 100 kg
        let new_amount = (plastic_weight_kg * pp_row.charges_per_unit) / 100;
        // Update the amount field
        frappe.model.set_value(pp_row.doctype, pp_row.name, 'deduction_amount', new_amount);

        // Refresh the child table
        frm.refresh_field('deductions');
    }
}


// Function to calculate plastic gross weight sum
function calculate_plastic_gross_weight(frm) {
    let plastic_weight = 0;
    if (frm.doc.inward_items) {
        frm.doc.inward_items.forEach(function(item) {
            if (item.item_bag_type && item.item_bag_type.toLowerCase() === 'plastic' && item.item_gross_weight) {
                plastic_weight += parseFloat(item.item_gross_weight) || 0;
            }
        });
    }
    return plastic_weight;
}

