function generate_simple_invoice_number(frm) {
    // Get current year
    let year = new Date().getFullYear();

    // Get count of existing Saudas this year to determine next number
    frappe.call({
        method: 'frappe.client.get_count',
        args: {
            doctype: 'Sauda',
            filters: {
                creation: ['>=', year + '-01-01'],
                sauda_invoice_no: ['like', 'INV-S-' + year + '%']
            }
        },
        callback: function(r) {
            let count = r.message || 0;
            let next_number = count + 1;
            let invoice_no = `INV-S-${year}-${String(next_number).padStart(4, '0')}`;

            // Check if this number already exists
            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: 'Sauda',
                    filters: {
                        sauda_invoice_no: invoice_no
                    },
                    limit: 1
                },
                callback: function(check_result) {
                    if (check_result.message && check_result.message.length > 0) {
                        // If exists, try next number
                        invoice_no = `INV-S-${year}-${String(next_number + 1).padStart(4, '0')}`;
                    }

                    frm.set_value('sauda_invoice_no', invoice_no);
                }
            });
        }
    });
}

