function verify_pan_button_click(frm) {
    // Validate PAN is entered
    if (!frm.doc.pan_number) {
        frappe.msgprint({
            title: __('Error'),
            indicator: 'red',
            message: __('Please enter PAN number to verify')
        });
        return;
    }

    // Show loading indicator
    frappe.show_alert({
        message: __('Verifying PAN...'),
        indicator: 'blue'
    }, 3);

    frappe.call({
        method: "kisan_warehouse.customers.doctype.customer.customer.verify_pan_api",
        args: {
            pan_number: frm.doc.pan_number,
            first_name: frm.doc.first_name,
            last_name: frm.doc.last_name,
            customer_type: frm.doc.customer_type
        },
        freeze: true,
        freeze_message: __("Verifying PAN, please wait..."),
        callback: function(r) {
            console.log("PAN Response Data:", r.message);

            if (r.message && r.message.status === "success") {
                // Set verified checkbox to 1
                frm.set_value('pan_verified', r.message.pan_verified || 1);

                // Auto-fill verification date
                if (r.message.pan_verified_date) {
                    frm.set_value("pan_verified_date", r.message.pan_verified_date);
                }

                // Auto-populate name fields if available - direct update without confirmation
                if (r.message.parsed_names && Object.keys(r.message.parsed_names).length > 0) {
                    const parsed = r.message.parsed_names;

                    // Directly update all available fields
                    if (parsed.first_name) {
                        frm.set_value('first_name', parsed.first_name);
                    }
                    if (parsed.middle_name) {
                        frm.set_value('middle_name', parsed.middle_name);
                    }
                    if (parsed.last_name) {
                        frm.set_value('last_name', parsed.last_name);
                    }
                }

                // Auto-populate address fields if available
                if (r.message.address_data && Object.keys(r.message.address_data).length > 0) {
                    const addr = r.message.address_data;

                        // No existing address, update directly
                        if (addr.address) frm.set_value('address', addr.address);
                        if (addr.city) frm.set_value('city', addr.city);
                        if (addr.state) frm.set_value('state', addr.state);
                        if (addr.zip) frm.set_value('zip', addr.zip);
                }

                show_pan_verification_success(frm, r.message);

                // Update verification icon
                update_pan_verification_icon(frm);

            } else if (r.message && r.message.status === "warning") {
                // Name mismatch - set verified to 0
                frm.set_value("pan_verified", 0);
                frm.set_value('pan_verified_date', null);

                let warning_msg = r.message.message || __("PAN name mismatch");
                if (r.message.registered_name) {
                    warning_msg += '<br><br>' + __('Registered Name: <b>{0}</b>', [r.message.registered_name]);

                    // Offer to update with registered name and address
                    if (r.message.parsed_names && Object.keys(r.message.parsed_names).length > 0) {
                            const parsed = r.message.parsed_names;
                                if (parsed.first_name) frm.set_value('first_name', parsed.first_name);
                                if (parsed.middle_name) frm.set_value('middle_name', parsed.middle_name);
                                if (parsed.last_name) frm.set_value('last_name', parsed.last_name);

                                // Update address fields if available
                                if (r.message.address_data) {
                                    const addr = r.message.address_data;
                                    if (addr.address) frm.set_value('address', addr.address);
                                    if (addr.city) frm.set_value('city', addr.city);
                                    if (addr.state) frm.set_value('state', addr.state);
                                    if (addr.zip) frm.set_value('zip', addr.zip);
                                }
                    } else {
                        frappe.msgprint({
                            title: __('Name Mismatch'),
                            indicator: 'orange',
                            message: warning_msg
                        });
                    }
                } else {
                    frappe.msgprint({
                        title: __('Name Mismatch'),
                        indicator: 'orange',
                        message: warning_msg
                    });
                }

                // Update verification icon
                update_pan_verification_icon(frm);

            } else {
                // Verification failed
                frm.set_value("pan_verified", 0);
                frm.set_value('pan_verified_date', null);

                frappe.msgprint({
                    title: __('Verification Failed'),
                    message: r.message?.message || __("PAN Verification Failed!"),
                    indicator: 'red'
                });

                // Update verification icon
                update_pan_verification_icon(frm);
            }
        },
        error: function(err) {
            frm.set_value("pan_verified", 0);
            frm.set_value('pan_verified_date', null);

            frappe.msgprint({
                title: __('Error'),
                indicator: 'red',
                message: __('An error occurred during verification. Please try again.')
            });

            // Update verification icon
            update_pan_verification_icon(frm);
        }
    });
}

// Show PAN Verification Success Message
function show_pan_verification_success(frm, result) {
    let msg = __('PAN verified successfully!');
    if (result.registered_name) {
        msg += '<br>' + __('Registered Name: {0}', [result.registered_name]);
    }

    frappe.show_alert({
        message: msg,
        indicator: 'green'
    }, 5);
}

// Update PAN Verification Icon beside button
function update_pan_verification_icon(frm) {
    // Check if verify_pan field exists
    if (!frm.fields_dict.verify_pan) {
        return;
    }

    const wrapper = frm.fields_dict.verify_pan.$wrapper;

    // Remove existing icon if any
    wrapper.find('.verification-status-icon').remove();

    // Center align the button and icon container
    wrapper.css({
        'display': 'flex',
        'align-items': 'center',
        'height': '100%',
        'padding-top': '20px'
    });

    // Add new icon based on verification status
    let icon_html = '';
    if (frm.doc.pan_verified === 1) {
        // Light green checkmark (verified)
        icon_html = `
            <span class="verification-status-icon" style="margin-left: 10px; display: inline-flex; align-items: center;" title="PAN Verified">
                <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <circle cx="10" cy="10" r="9" fill="#98D85B" stroke="#98D85B" stroke-width="1"/>
                    <path d="M6 10L8.5 12.5L14 7" stroke="white" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                </svg>
            </span>
        `;
    } else {
        // Red cross (not verified)
        icon_html = `
            <span class="verification-status-icon" style="margin-left: 10px; display: inline-flex; align-items: center;" title="PAN Not Verified">
                <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <circle cx="10" cy="10" r="9" fill="#F06548" stroke="#F06548" stroke-width="1"/>
                    <path d="M7 7L13 13M13 7L7 13" stroke="white" stroke-width="2" stroke-linecap="round"/>
                </svg>
            </span>
        `;
    }

    // Append icon after the button
    wrapper.find('.btn').after(icon_html);

    // Style the button based on verification status
    const btn = wrapper.find('.btn');
    btn.css({
        'display': 'inline-flex',
        'align-items': 'center',
        'justify-content': 'center'
    });

    if (frm.doc.pan_verified === 1) {
        btn.removeClass('btn-default').addClass('btn-success').css({
            'background-color': '#98D85B',
            'border-color': '#98D85B',
            'color': 'white'
        });
    } else {
        btn.removeClass('btn-success').addClass('btn-default').css({
            'background-color': '',
            'border-color': '',
            'color': ''
        });
    }
}

