// GSTIN Verify Button Click Handler
function verify_gstin_button_click(frm) {
    // Validate GSTIN is entered
    if (!frm.doc.gstin) {
        frappe.msgprint({
            title: __('Error'),
            indicator: 'red',
            message: __('Please enter GSTIN to verify')
        });
        return;
    }

    if (frm.doc.customer_type !== 'Company / Trader') {
        frappe.msgprint({
            title: __('Error'),
            indicator: 'red',
            message: __('GSTIN verification is only for Company / Trader type')
        });
        return;
    }

    // Show loading indicator
    frappe.show_alert({
        message: __('Verifying GSTIN...'),
        indicator: 'blue'
    }, 3);

    frappe.call({
        method: "kisan_warehouse.customers.doctype.customer.customer.verify_gstin_api",
        args: {
            gstin: frm.doc.gstin,
            first_name: frm.doc.first_name
        },
        freeze: true,
        freeze_message: __("Verifying GSTIN, please wait..."),
        callback: function(r) {
            console.log("GSTIN Response Data:", r.message);

            if (r.message && r.message.status === "success") {
                // Set verified checkbox (hidden) to 1
                frm.set_value('gstin_verified', r.message.gstin_verified || 1);

                // Auto-fill verification date
                if (r.message.gstin_verified_date) {
                    frm.set_value("gstin_verified_date", r.message.gstin_verified_date);
                }

                // Auto-populate first_name (business name) if available
                if (r.message.first_name) {
                    if (!frm.doc.first_name) {
                        // Empty field - directly fill
                        frm.set_value('first_name', r.message.first_name);
                        show_gstin_verification_success(frm, r.message);
                    } else if (frm.doc.first_name.toLowerCase().trim() !== r.message.first_name.toLowerCase().trim()) {
                        // Field has different value - ask for confirmation

                        frm.set_value('first_name', r.message.first_name);
                        show_gstin_verification_success(frm, r.message);
                        return; // Exit to prevent double success message
                    } else {
                        // Same value - just show success
                        show_gstin_verification_success(frm, r.message);
                    }
                } else {
                    show_gstin_verification_success(frm, r.message);
                }

                // Reset and auto-fill address fields from GSTIN response
                if (r.message.data) {
                    const data = r.message.data;

                    // Check for principal_place_split_address
                    if (data.principal_place_split_address) {
                        const split_addr = data.principal_place_split_address;

                        // Reset and fill city
                        if (split_addr.city) {
                            frm.set_value('city', split_addr.city);
                        }

                        // Reset and fill state
                        if (split_addr.state) {
                            frm.set_value('state', split_addr.state);
                        }

                        // Reset and fill zip
                        if (split_addr.pincode) {
                            frm.set_value('zip', split_addr.pincode);
                        }
                    }

                    // Reset and fill address
                    if (data.principal_place_address) {
                        frm.set_value('address', data.principal_place_address);
                    }
                }

                // Update verification icon
                update_gstin_verification_icon(frm);

            }else {
                // Verification failed
                frm.set_value("gstin_verified", 0);
                frm.set_value('gstin_verified_date', null);

                frappe.msgprint({
                    title: __('Verification Failed'),
                    message: r.message?.message || __("GSTIN Verification Failed!"),
                    indicator: 'red'
                });

                // Update verification icon
                update_gstin_verification_icon(frm);
            }
        },
        error: function(err) {
            frm.set_value("gstin_verified", 0);
            frm.set_value('gstin_verified_date', null);

            frappe.msgprint({
                title: __('Error'),
                indicator: 'red',
                message: __('An error occurred during verification. Please try again.')
            });

            // Update verification icon
            update_gstin_verification_icon(frm);
        }
    });
}

