// ==================== AADHAAR VERIFICATION FUNCTIONS ====================

// Send OTP Button Click Handler
function send_aadhaar_otp_click(frm) {
    // Validate Aadhaar is entered
    if (!frm.doc.aadhar_number) {
        frappe.msgprint({
            title: __('Error'),
            indicator: 'red',
            message: __('Please enter Aadhaar number')
        });
        return;
    }

    // Validate Aadhaar format (12 digits)
    const aadhaar_clean = frm.doc.aadhar_number.replace(/\s+/g, '');
    if (!/^\d{12}$/.test(aadhaar_clean)) {
        frappe.msgprint({
            title: __('Validation Error'),
            indicator: 'red',
            message: __('Aadhaar number must be 12 digits')
        });
        return;
    }

    // Show loading indicator
    frappe.show_alert({
        message: __('Sending OTP...'),
        indicator: 'blue'
    }, 3);

    frappe.call({
        method: "kisan_warehouse.customers.doctype.customer.customer.send_aadhaar_otp",
        args: {
            aadhaar_number: frm.doc.aadhar_number
        },
        freeze: true,
        freeze_message: __("Sending OTP, please wait..."),
        callback: function(r) {
            console.log("OTP Response:", r.message);

            if (r.message && r.message.status === "success") {
                // Store reference_id in hidden field
                frm.set_value('aadhar_reference_id', r.message.reference_id);

                // Show OTP section
                toggle_otp_section(frm, true);

                // Clear previous OTP if any
                frm.set_value('aadhar_otp', '');

                frappe.show_alert({
                    message: __('OTP sent successfully! Please check your registered mobile.'),
                    indicator: 'green'
                }, 7);

                // Focus on OTP field
                setTimeout(() => {
                    frm.fields_dict.aadhar_otp.$input.focus();
                }, 500);

                // Change button color to indicate OTP sent
                update_send_otp_button_style(frm, true);

            } else {
                frappe.msgprint({
                    title: __('Failed'),
                    message: r.message?.message || __("Failed to send OTP"),
                    indicator: 'red'
                });
                toggle_otp_section(frm, false);
                update_send_otp_button_style(frm, false);
            }
        },
        error: function(err) {
            frappe.msgprint({
                title: __('Error'),
                indicator: 'red',
                message: __('An error occurred while sending OTP. Please try again.')
            });
            toggle_otp_section(frm, false);
            update_send_otp_button_style(frm, false);
        }
    });
}

// Verify Aadhaar OTP Button Click Handler
function verify_aadhaar_otp_click(frm) {
    // Validate reference_id exists
    if (!frm.doc.aadhar_reference_id) {
        frappe.msgprint({
            title: __('Error'),
            indicator: 'red',
            message: __('Please send OTP first')
        });
        return;
    }

    // Validate OTP is entered
    if (!frm.doc.aadhar_otp) {
        frappe.msgprint({
            title: __('Error'),
            indicator: 'red',
            message: __('Please enter OTP')
        });
        return;
    }

    // Validate OTP format (6 digits)
    const otp_clean = frm.doc.aadhar_otp.replace(/\s+/g, '');
    if (!/^\d{6}$/.test(otp_clean)) {
        frappe.msgprint({
            title: __('Validation Error'),
            indicator: 'red',
            message: __('OTP must be 6 digits')
        });
        return;
    }

    // Show loading indicator
    frappe.show_alert({
        message: __('Verifying Aadhaar...'),
        indicator: 'blue'
    }, 3);

    frappe.call({
        method: "kisan_warehouse.customers.doctype.customer.customer.verify_aadhaar_otp",
        args: {
            reference_id: frm.doc.aadhar_reference_id,
            otp: frm.doc.aadhar_otp
        },
        freeze: true,
        freeze_message: __("Verifying Aadhaar, please wait..."),
        callback: function(r) {
            console.log("Aadhaar Verification Response:", r.message);

            if (r.message && r.message.status === "success") {
                // Set verified checkbox to 1
                frm.set_value('aadhar_verified', 1);

                // Auto-fill verification date
                if (r.message.aadhar_verified_date) {
                    frm.set_value("aadhar_verified_date", r.message.aadhar_verified_date);
                }

                // Auto-populate name fields if available
                if (r.message.parsed_names && Object.keys(r.message.parsed_names).length > 0) {
                    const parsed = r.message.parsed_names;

                    if (parsed.first_name) {
                        frm.set_value('first_name', parsed.first_name);
                    }
                    if (parsed.middle_name) {
                        frm.set_value('middle_name', parsed.middle_name);
                    }
                    if (parsed.last_name) {
                        frm.set_value('last_name', parsed.last_name);
                    }
                }

                // Auto-populate address fields if available
                if (r.message.address_data && Object.keys(r.message.address_data).length > 0) {
                    const addr = r.message.address_data;

                    if (addr.address) frm.set_value('address', addr.address);
                    if (addr.city) frm.set_value('city', addr.city);
                    if (addr.state) frm.set_value('state', addr.state);
                    if (addr.zip) frm.set_value('zip', addr.zip);
                }

                frappe.show_alert({
                    message: __('Aadhaar verified successfully!'),
                    indicator: 'green'
                }, 5);

                // Hide OTP section after successful verification
                toggle_otp_section(frm, false);

                // Clear OTP field
                frm.set_value('aadhar_otp', '');

                // Update verification icon
                update_aadhaar_verification_icon(frm);
                update_send_otp_button_style(frm, false);

            } else {
                // Verification failed
                frm.set_value("aadhar_verified", 0);
                frm.set_value('aadhar_verified_date', null);

                let error_message = r.message?.message || __("Aadhaar verification failed. Please check OTP and try again.");

                // Show debug data if available
                if (r.message?.debug_data) {
                    console.error("Aadhaar Verification Debug Data:", r.message.debug_data);

                    // Add more specific error message if available
                    const debug = r.message.debug_data;
                    if (debug.message) {
                        error_message = debug.message;
                    } else if (debug.data && debug.data.message) {
                        error_message = debug.data.message;
                    }
                }

                frappe.msgprint({
                    title: __('Verification Failed'),
                    message: error_message,
                    indicator: 'red'
                });

                // Update verification icon
                update_aadhaar_verification_icon(frm);
            }
        },
        error: function(err) {
            frm.set_value("aadhar_verified", 0);
            frm.set_value('aadhar_verified_date', null);

            frappe.msgprint({
                title: __('Error'),
                indicator: 'red',
                message: __('An error occurred during verification. Please try again.')
            });

            // Update verification icon
            update_aadhaar_verification_icon(frm);
        }
    });
}

// Toggle OTP Section Visibility
function toggle_otp_section(frm, show) {
    if (show) {
        // frm.set_df_property('section_break_aadhar_otp', 'hidden', 0);
        frm.set_df_property('aadhar_otp', 'hidden', 0);
        frm.set_df_property('verify_aadhar_button', 'hidden', 0);
    } else {
        // frm.set_df_property('section_break_aadhar_otp', 'hidden', 1);
        frm.set_df_property('aadhar_otp', 'hidden', 1);
        frm.set_df_property('verify_aadhar_button', 'hidden', 1);
    }
    frm.refresh_fields();
}

