        // Red cross (not verified)
        icon_html = `
            <span class="verification-status-icon" style="margin-left: 10px; display: inline-flex; align-items: center;" title="Aadhaar Not Verified">
                <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <circle cx="10" cy="10" r="9" fill="#F06548" stroke="#F06548" stroke-width="1"/>
                    <path d="M7 7L13 13M13 7L7 13" stroke="white" stroke-width="2" stroke-linecap="round"/>
                </svg>
            </span>
        `;
    }

    // Append icon after the button
    wrapper.find('.btn').after(icon_html);

    // Style the button based on verification status
    const btn = wrapper.find('.btn');
    btn.css({
        'display': 'inline-flex',
        'align-items': 'center',
        'justify-content': 'center'
    });

    if (frm.doc.aadhar_verified === 1) {
        btn.removeClass('btn-default btn-info').addClass('btn-success').css({
            'background-color': '#98D85B',
            'border-color': '#98D85B',
            'color': 'white'
        });
    }
}

function style_verify_button(frm) {
    if (!frm.fields_dict.verify_aadhar_button) {
        return;
    }

    const wrapper = frm.fields_dict.verify_aadhar_button.$wrapper;
    wrapper.css({
        'padding-top': '20px'  // Align with OTP field
    });
}

frappe.ui.form.on('Sauda', {
    broker: function(frm) {
        if (frm.doc.customer) {
            frappe.call({
                method: "frappe.client.get_value",
                args: {
                    doctype: "Broker",
                    filters: { name: frm.doc.broker },
                    fieldname: "email" 
                },
                callback: function(r) {
                    if (r.message && r.message.email) {
                        frm.set_value('broker_email', r.message.email);
                    } else {
                        frm.set_value('broker_email', '');
                    }
                }
            });
        } else {
            frm.set_value('broker_email', '');
        }
    }
});
