script:
frappe.ui.form.on('Inward', {
    refresh: function(frm) {
        setup_deduction_types(frm);
    }
});

frappe.ui.form.on('Inward Deduction', {
    deductions_add: function(frm, cdt, cdn) {
        setup_deduction_types(frm);
    },

    deduction_type: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        if (row.deduction_type) {
            frappe.call({
                method: 'frappe.desk.form.load.getdoc',
                args: {
                    doctype: 'App Settings',
                    name: 'App Settings'
                },
                callback: function(r) {
                    if (r.docs && r.docs[0] && r.docs[0].default_deduction_types) {
                        r.docs[0].default_deduction_types.forEach(function(settings_row) {
                            if (settings_row.deduction_name === row.deduction_type && settings_row.is_active) {
                                frappe.model.set_value(cdt, cdn, 'amount', settings_row.default_amount || 0);
                                frappe.model.set_value(cdt, cdn, 'description', settings_row.description || '');
                            }
                        });
                    }
                }
            });
        }
    }
});

function setup_deduction_types(frm) {
    frappe.call({
        method: 'frappe.desk.form.load.getdoc',
        args: {
            doctype: 'App Settings',
            name: 'App Settings'
        },
        callback: function(r) {
            if (r.docs && r.docs[0] && r.docs[0].default_deduction_types) {
                let options = [];
                r.docs[0].default_deduction_types.forEach(function(row) {
                    if (row.is_active) {
                        options.push(row.deduction_name);
                    }
                });

                if (options.length > 0) {
                    frm.fields_dict.deductions.grid.update_docfield_property('deduction_type', 'options', options.join('\n'));
                    frm.fields_dict.deductions.grid.refresh();
                }
            }
        }
    });
}

frappe.ui.form.on('Sauda', {
    refresh: function(frm) {
        calculate_pending_values(frm);
    },

    validate: function(frm) {
        // Client-side validation before save/submit
        validate_required_values(frm);
    },

    expected_quantity: function(frm) {
        calculate_total_amount(frm);
        calculate_pending_values(frm);
    },

    sauda_rate: function(frm) {
        calculate_total_amount(frm);
        calculate_pending_values(frm);
    },

    total_amount: function(frm) {
        calculate_pending_values(frm);
    },

    booking_date: function(frm) {
        calculate_dates(frm);
    },

    delivery_duration: function(frm) {
        calculate_dates(frm);
    },

    payment_duration: function(frm) {
        calculate_dates(frm);
    }
});

function validate_required_values(frm) {
    // Prevent submission with zero or invalid values
    if (!frm.doc.expected_quantity || frm.doc.expected_quantity <= 0) {
        frappe.throw(__("Expected Quantity must be greater than 0"));
        return false;
    }

    if (!frm.doc.sauda_rate || frm.doc.sauda_rate <= 0) {
        frappe.throw(__("Sauda Rate must be greater than 0"));
        return false;
    }

    if (!frm.doc.delivery_duration || frm.doc.delivery_duration <= 0) {
        frappe.throw(__("Delivery Duration must be greater than 0"));
        return false;
    }

    if (!frm.doc.payment_duration || frm.doc.payment_duration <= 0) {
        frappe.throw(__("Payment Duration must be greater than 0"));
        return false;
    }

    return true;
}

function calculate_total_amount(frm) {
    if (frm.doc.expected_quantity >= 0 && frm.doc.sauda_rate >= 0) {
        let total = frm.doc.expected_quantity * frm.doc.sauda_rate;
        frm.set_value('total_amount', total);
    }
}

function calculate_dates(frm) {
    let booking_date = frm.doc.booking_date;
    let delivery_duration = frm.doc.delivery_duration;
    let payment_duration = frm.doc.payment_duration;

    if (booking_date) {
        if (delivery_duration > 0) {
            let delivery_end_date = frappe.datetime.add_days(booking_date, delivery_duration);
            frm.set_value('delivery_end_date', delivery_end_date);
        }

        if (payment_duration > 0) {
            let payment_end_date = frappe.datetime.add_days(booking_date, payment_duration);
            frm.set_value('payment_end_date', payment_end_date);
        }
    }
}

function calculate_pending_values(frm) {
    let is_editable = frm.is_new() || frm.doc.docstatus == 0;

    if (frm.doc.expected_quantity >= 0 && is_editable) {
        frm.set_value('pending_quantity', frm.doc.expected_quantity);
    }

    if (frm.doc.total_amount >= 0 && is_editable) {
        frm.set_value('pending_total_amount', frm.doc.total_amount);
    }

    frm.refresh_fields(['pending_quantity', 'pending_total_amount']);
}

// Calculate item_amount for each row in inward_items child table
frappe.ui.form.on('Inward Item Detail', {
    item_arrival_weight: function(frm, cdt, cdn) {
        calculate_item_amount(frm, cdt, cdn);
        calculate_parent_totals(frm);
        calculate_financial_totals(frm);
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    },

    item_rate: function(frm, cdt, cdn) {
        calculate_item_amount(frm, cdt, cdn);
        calculate_parent_totals(frm);
        calculate_financial_totals(frm);
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    },

    item_gross_weight: function(frm, cdt, cdn) {
        calculate_parent_totals(frm);
        calculate_financial_totals(frm);
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    },

    bags: function(frm, cdt, cdn) {
        calculate_parent_totals(frm);
    },

    // Trigger when rows are added or removed
    inward_items_add: function(frm, cdt, cdn) {
        calculate_parent_totals(frm);
        calculate_financial_totals(frm);
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    },

    inward_items_remove: function(frm, cdt, cdn) {
        calculate_parent_totals(frm);
        calculate_financial_totals(frm);
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    }
});

// Calculate deductions when deductions child table changes
frappe.ui.form.on('Inward Deduction', {
    amount: function(frm, cdt, cdn) {
        calculate_deduction_totals(frm);
        calculate_financial_totals(frm);
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    },

    // Trigger when deduction rows are added or removed
    deductions_add: function(frm, cdt, cdn) {
        calculate_deduction_totals(frm);
        calculate_financial_totals(frm);
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    },

    deductions_remove: function(frm, cdt, cdn) {
        calculate_deduction_totals(frm);
        calculate_financial_totals(frm);
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    }
});

// Tax percentage field triggers
frappe.ui.form.on('Inward', {
    cgst_percent: function(frm) {
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    },

    sgst_percent: function(frm) {
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    },

    igst_percent: function(frm) {
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    },

    tcs_percent: function(frm) {
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    },

    tds_percent: function(frm) {
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    },

    broker_commission_percent: function(frm) {
        calculate_tax_totals(frm);
        calculate_net_total(frm);
    }
});

function calculate_item_amount(frm, cdt, cdn) {
    let row = locals[cdt][cdn];

    // Get values, default to 0 if empty
    let arrival_weight = flt(row.item_arrival_weight) || 0;
    let rate = flt(row.item_rate) || 0;

    // Calculate: item_amount = (item_arrival_weight / 100) Ã— item_rate
    let item_amount = (arrival_weight / 100) * rate;

    // Set the calculated value
    frappe.model.set_value(cdt, cdn, 'item_amount', item_amount);

    // Refresh the field to show updated value
    frm.refresh_field('inward_items');
}

function calculate_parent_totals(frm) {
    let total_gross_weight = 0;
    let total_bags = 0;
    let total_arrival_weight = 0;
    let total_amount = 0;

    // Loop through all rows in inward_items child table
    if (frm.doc.inward_items) {
        frm.doc.inward_items.forEach(function(row) {
            total_gross_weight += flt(row.item_gross_weight) || 0;
            total_bags += flt(row.bags) || 0;
            total_arrival_weight += flt(row.item_arrival_weight) || 0;
            total_amount += flt(row.item_amount) || 0;
        });
    }

    // Set parent totals
    frm.set_value('total_gross_weight', total_gross_weight);
    frm.set_value('total_bags', total_bags);
    frm.set_value('total_arrival_weight', total_arrival_weight);
    frm.set_value('total_amount', total_amount);
}

function calculate_deduction_totals(frm) {
    let total_deductions = 0;

    // Loop through all rows in deductions child table
    if (frm.doc.deductions) {
        frm.doc.deductions.forEach(function(row) {
            total_deductions += flt(row.amount) || 0;
        });
    }

    // Set total deductions
    frm.set_value('total_deductions', total_deductions);
}

function calculate_financial_totals(frm) {
    let total_amount = flt(frm.doc.total_amount) || 0;
    let total_deductions = flt(frm.doc.total_deductions) || 0;

    // Calculate sub_total = total_amount - total_deductions
    let sub_total = total_amount - total_deductions;

    // Set sub total
    frm.set_value('sub_total', sub_total);
}

function calculate_tax_totals(frm) {
    let sub_total = flt(frm.doc.sub_total) || 0;

    // Get tax percentages
    let cgst_percent = flt(frm.doc.cgst_percent) || 0;
    let sgst_percent = flt(frm.doc.sgst_percent) || 0;
    let igst_percent = flt(frm.doc.igst_percent) || 0;
    let tcs_percent = flt(frm.doc.tcs_percent) || 0;
    let tds_percent = flt(frm.doc.tds_percent) || 0;
    let broker_commission_percent = flt(frm.doc.broker_commission_percent) || 0;

    // Calculate tax amounts
    let total_gst_amount = sub_total * (cgst_percent + sgst_percent + igst_percent) / 100;
    let tcs_amount = sub_total * tcs_percent / 100;
    let tds_amount = sub_total * tds_percent / 100;
    let broker_commission_amount = sub_total * broker_commission_percent / 100;

    // Set tax amounts
    frm.set_value('total_gst_amount', total_gst_amount);
    frm.set_value('tcs_amount', tcs_amount);
    frm.set_value('tds_amount', tds_amount);
    frm.set_value('broker_commission_amount', broker_commission_amount);
}

function calculate_net_total(frm) {
    let sub_total = flt(frm.doc.sub_total) || 0;
    let total_gst_amount = flt(frm.doc.total_gst_amount) || 0;
    let tcs_amount = flt(frm.doc.tcs_amount) || 0;
    let tds_amount = flt(frm.doc.tds_amount) || 0;

    // Calculate net_total = sub_total + total_gst_amount + tcs_amount - tds_amount
    let net_total = sub_total + total_gst_amount + tcs_amount - tds_amount;

    // Set net total
    frm.set_value('net_total', net_total);
}
