html:
{# Compact Inward Receipt - Enhanced vendor/doc info section #}
<style>
  @page { size: A4; margin: 4mm 6mm 35mm 6mm; }
  html, body { height: 100%; margin: 0; padding: 0; font-family: Arial, sans-serif; color:#000; position: relative; }
  .wrap { position: relative; min-height: calc(100vh - 35mm); padding-bottom: 50px; }
  /* Global small font & compact spacing */
  body, table, th, td, div { font-size: 8.5px; line-height:0.8; }
  table { width:100%; border-collapse: collapse; }
  th, td { padding: 0px 2px; vertical-align: middle; height: 8px; }
  th { background: #f6f6f6; font-weight:700; }
  .no-border td { border: none; padding: 0px 1px; }
  .border td, .border th { border: 1px solid #222; }
  .center { text-align:center; }
  .left { text-align:left; }
  .right { text-align:right; }
  .section-title { font-weight:700; margin:0.5px 0 0.5px 0; font-size:8px; }
  /* Make tables compact and try to avoid page-breaks inside */
  .item-table, .deduction-table { page-break-inside: avoid; -webkit-column-break-inside: avoid; -moz-page-break-inside: avoid; margin-bottom:1.5px; }
  .border { page-break-inside: avoid; }
  /* Header compact */
  .hdr-logo { max-height:28px; display:block; }
  .hdr-company { font-size:9.5px; font-weight:800; line-height:1.1; }
  .hdr-addr { font-size:7px; margin-top:0.5px; line-height:1.1; }

  /* ENHANCED VENDOR/DOC INFO SECTION */
  .vendor-doc-table {
    border: 2px solid #000 !important;
    margin-bottom: 3px;
    border-collapse: collapse !important;
  }
  .vendor-doc-table td {
    border: 1px solid #000 !important;
    padding: 3px 5px !important;
    vertical-align: middle !important;
    line-height: 1.3 !important;
    font-size: 11px !important;
    height: 18px !important;
  }
  .vendor-doc-table .field-label {
    font-weight: 700;
    font-size: 11.5px !important;
    color: #000;
    display: inline-block;
    min-width: 130px;
  }
  .vendor-doc-table .field-value {
    font-size: 11.5px !important;
    color: #000;
  }

  /* Footer as normal flow element */
  .footer {
    width: 100%;
    font-size:7px;
    padding: 4px 0 0 0;
    margin-top: 6px;
    border-top: 1px solid #ddd;
    page-break-inside: avoid;
    position: absolute;
    bottom: 0;
    left: 0;
  }

  @media print {
    .footer {
      page-break-inside: avoid;
      -webkit-print-color-adjust: exact;
      print-color-adjust: exact;
      position: fixed;
      bottom: 0;
      left: 0;
      right: 0;
    }
    body, table, th, td, div { font-size: 8.5px; line-height: 0.8; }
    th, td { padding: 0px 2px; height: 8px; }
    .section-title { margin: 3px 0 3px 0; }
    .item-table, .deduction-table { margin-bottom: 1.5px; }

    /* Keep enhanced styles in print */
    .vendor-doc-table td {
      font-size: 11px !important;
      padding: 3px 5px !important;
      border: 1px solid #000 !important;
      height: 18px !important;
    }
    .vendor-doc-table .field-label,
    .vendor-doc-table .field-value {
      font-size: 11.5px !important;
    }
  }
  .signature-box { text-align:center; padding-top:3px; }
  /* small helpers */
  .muted { color:#333; font-size:7px; }
  .highlight { font-weight:700; background:#f3f3f3; }
  /* reduce word-wrapping for product names/fields to save vertical space */
  .nowrap { white-space:nowrap; }
</style>

{% set comp = None %}
{% if doc.company %}
  {% set comp = frappe.get_doc('Company', doc.company) %}
{% endif %}

{% set customer_name = frappe.db.get_value('Customer', doc.customer, 'first_name') if doc.customer else '' %}
{% set customer_gstin = frappe.db.get_value('Customer', doc.customer, 'gstin') if doc.customer else '' %}
{% set broker_name = frappe.db.get_value('Broker', doc.broker, 'first_name') if doc.broker else '' %}
{% set warehouse_name = frappe.db.get_value('Warehouse', doc.warehouse, 'warehouse_name') if doc.warehouse else '' %}

{# Fetch customer location (city) from Customer doctype #}
{% set customer_location = '' %}
{% if doc.customer %}
  {% set customer_location = frappe.db.get_value('Customer', doc.customer, 'city') or '' %}
{% endif %}

<div class="wrap">
  <!-- HEADER -->
  <table class="no-border">
    <tr>
      <td style="width:15%;">
        {% if comp and comp.get('logo') %}
          <img class="hdr-logo" src="{{ frappe.utils.get_url() }}{{ comp.get('logo') }}">
        {% endif %}
      </td>
      <td style="width:70%;" class="center">
        <div class="hdr-company">{{ comp.get('company_name') if comp else (doc.company or '') }}</div>
        <div class="hdr-addr">
          {% if comp %}
            {{ comp.get('address') or '' }}{% if comp.get('city') %}, {{ comp.get('city') }}{% endif %}{% if comp.get('state') %}, {{ comp.get('state') }}{% endif %}{% if comp.get('pincode') %} - {{ comp.get('pincode') }}{% endif %}
          {% endif %}
        </div>
      </td>
      <td style="width:15%;"></td>
    </tr>
  </table>

  <div class="center" style="font-size:9px; font-weight:700; margin-top:1.5px; margin-bottom:1.5px;">INWARD RECEIPT / PURCHASE SETTLEMENT ADVICE</div>

  <!-- ENHANCED VENDOR / DOC INFO - Table with proper rows and columns -->
  <table class="vendor-doc-table">
    <tr>
      <td style="width:25%; border-right: 1px solid #000 !important;">
        <span class="field-label">Vendor:</span>
      </td>
      <td style="width:25%; border-right: 2px solid #000 !important;">
        <span class="field-value">{{ customer_name }}</span>
      </td>
      <td style="width:25%; border-right: 1px solid #000 !important;">
        <span class="field-label">Invoice No:</span>
      </td>
      <td style="width:25%;">
        <span class="field-value">{{ doc.inward_invoice_no or doc.name }}</span>
      </td>
    </tr>
    <tr>
      <td style="border-right: 1px solid #000 !important;">
        <span class="field-label">Vendor Location:</span>
      </td>
      <td style="border-right: 2px solid #000 !important;">
        <span class="field-value">{{ customer_location }}</span>
      </td>
      <td style="border-right: 1px solid #000 !important;">
        <span class="field-label">Warehouse:</span>
      </td>
      <td>
        <span class="field-value">{{ warehouse_name }}</span>
      </td>
    </tr>
    <tr>
      <td style="border-right: 1px solid #000 !important;">
        <span class="field-label">Vendor GSTIN:</span>
      </td>
      <td style="border-right: 2px solid #000 !important;">
        <span class="field-value">{{ customer_gstin }}</span>
      </td>
      <td style="border-right: 1px solid #000 !important;">
        <span class="field-label">Our GSTIN:</span>
      </td>
      <td>
        <span class="field-value">{{ doc.vendor_gstin or '' }}</span>
      </td>
    </tr>
    <tr>
      <td style="border-right: 1px solid #000 !important;">
        <span class="field-label">Vendor Doc No:</span>
      </td>
      <td style="border-right: 2px solid #000 !important;">
        <span class="field-value">{{ doc.vendor_doc_no or '' }}</span>
      </td>
      <td style="border-right: 1px solid #000 !important;">
        <span class="field-label">Bill Date:</span>
      </td>
      <td>
        <span class="field-value">{{ frappe.utils.format_datetime(doc.bill_date) if doc.bill_date else '' }}</span>
      </td>
    </tr>
    <tr>
      <td style="border-right: 1px solid #000 !important;">
        <span class="field-label">Vendor Doc Date:</span>
      </td>
      <td style="border-right: 2px solid #000 !important;">
        <span class="field-value">{{ frappe.utils.formatdate(doc.vendor_date) if doc.vendor_date else '' }}</span>
      </td>
      <td style="border-right: 1px solid #000 !important;">
        <span class="field-label">Arrival Date:</span>
      </td>
      <td>
        <span class="field-value">{{ frappe.utils.format_datetime(doc.arrival_date) if doc.arrival_date else '' }}</span>
      </td>
    </tr>
    <tr>
      <td style="border-right: 1px solid #000 !important;">
        <span class="field-label">Vendor Bill Amount:</span>
      </td>
      <td style="border-right: 2px solid #000 !important;">
        <span class="field-value">{{ '%.2f' % frappe.utils.flt(doc.vendor_amount or 0) }}</span>
      </td>
      <td style="border-right: 1px solid #000 !important;">
        <span class="field-label">Lorry No:</span>
      </td>
      <td>
        <span class="field-value">{{ doc.vehicle_no or '' }}</span>
      </td>
    </tr>
    <tr>
      <td style="border-right: 1px solid #000 !important;">
        <span class="field-label">Broker:</span>
      </td>
      <td style="border-right: 2px solid #000 !important;">
        <span class="field-value">{{ broker_name }}</span>
      </td>
      <td style="border-right: 1px solid #000 !important;">

      </td>
      <td>

      </td>
    </tr>
  </table>

  <!-- ITEM DETAILS -->
  <div class="section-title">Item Details</div>
  <table class="border item-table">
    <thead>
      <tr>
        <th style="width:4%;">Sr</th>
        <th style="width:26%;">Item Name</th>
        <th style="width:8%;">HSN</th>
        <th style="width:10%;">Bag Type</th>
        <th style="width:8%;">No. of Bags</th>
        <th style="width:12%;">Gross Wt (kg)</th>
        <th style="width:12%;">Arr. Wt (kg)</th>
        <th style="width:10%;">Rate (Qtl)</th>
        <th style="width:10%;">Amount</th>
      </tr>
    </thead>
    <tbody>
      {% for row in doc.inward_items %}
      {% set bags = frappe.utils.flt(row.item_bags or 0) %}
      {% set gross = frappe.utils.flt(row.item_gross_weight or 0) %}
      {% set arrw = frappe.utils.flt(row.item_arrival_weight or 0) %}
      {% set rate = frappe.utils.flt(row.item_rate or 0) %}
      {% set amt = frappe.utils.flt(row.item_amount or 0) %}
      {% set prod_name = frappe.db.get_value('Product', doc.product, 'product_name') if doc.product else '' %}
      {% set hsn_val = frappe.db.get_value('Product', doc.product, 'hsn_code') if doc.product else '' %}
      <tr>
        <td class="center nowrap">{{ loop.index }}</td>
        <td class="left" style="padding-left:4px;">{{ prod_name }}</td>
        <td class="center nowrap">{{ hsn_val }}</td>
        <td class="left nowrap">{{ row.item_bag_type or '' }}</td>
        <td class="right nowrap">{{ '%.2f' % bags }}</td>
        <td class="right nowrap">{{ '%.2f' % gross }}</td>
        <td class="right nowrap">{{ '%.2f' % arrw }}</td>
        <td class="right nowrap">{{ '%.2f' % rate }}</td>
        <td class="right nowrap">{{ '%.2f' % amt }}</td>
      </tr>
      {% endfor %}

      {% set total_bags = frappe.utils.flt(doc.inward_total_bags or 0) %}
      {% set total_gross = frappe.utils.flt(doc.total_gross_weight or 0) %}
      {% set total_arr = frappe.utils.flt(doc.total_arrival_weight or 0) %}
      {% set total_amt = frappe.utils.flt(doc.total_amount or 0) %}
      <tr class="highlight">
        <td colspan="4" class="right"><b>Total</b></td>
        <td class="right nowrap">{{ '%.2f' % total_bags }}</td>
        <td class="right nowrap">{{ '%.2f' % total_gross }}</td>
        <td class="right nowrap">{{ '%.2f' % total_arr }}</td>
        <td></td>
        <td class="right nowrap">{{ '%.2f' % total_amt }}</td>
      </tr>
    </tbody>
  </table>

  <!-- Deductions -->
  <div class="section-title">Deductions</div>
  <table class="border deduction-table" style="margin-bottom:1.5px;">
    <thead>
      <tr>
        <th style="width:4%;">Sr</th>
        <th style="width:46%;">Particulars</th>
        <th style="width:12%;">Required</th>
        <th style="width:12%;">Actual</th>
        <th style="width:12%;">Difference</th>
        <th style="width:14%;">Amount</th>
      </tr>
    </thead>
    <tbody>
      {% for row in doc.deductions %}
      {% set req = frappe.utils.flt(row.required_value or 0) %}
      {% set act = frappe.utils.flt(row.actual_value or 0) %}
      {% set diff = frappe.utils.flt(row.difference_value or 0) %}
      {% set damt = frappe.utils.flt(row.deduction_amount or 0) %}
      <tr>
        <td class="center nowrap">{{ loop.index }}</td>
        <td class="left" style="padding-left:4px;">{{ row.deduction_type or '' }}</td>
        <td class="right nowrap">{{ '%.2f' % req }}</td>
        <td class="right nowrap">{{ '%.2f' % act }}</td>
        <td class="right nowrap">{{ '%.2f' % diff }}</td>
        <td class="right nowrap">{{ '%.2f' % damt }}</td>
      </tr>
      {% endfor %}
      {% set total_ded = frappe.utils.flt(doc.total_deductions or 0) %}
      <tr class="highlight">
        <td colspan="5" class="right"><b>Total Deductions</b></td>
        <td class="right nowrap">{{ '%.2f' % total_ded }}</td>
      </tr>
    </tbody>
  </table>

  <!-- Sub Total Row -->
  <table class="border" style="margin-top:-1px; margin-bottom:1.5px;">
    <tr class="highlight">
      <td colspan="5" class="right" style="width:86%;"><b>Sub Total</b></td>
      <td class="right nowrap" style="width:14%;"><b>{{ '%.2f' % (total_amt - total_ded) }}</b></td>
    </tr>
  </table>

  <!-- Add Sub Total row after Total Deductions -->
  {% set sub_total = total_amt - total_ded %}

  <!-- DEBIT NOTE DETAILS -->
  {% if doc.debit_note and doc.debit_note|length > 0 %}
  <div style="width:38%; float:left; margin-top:2px;">
    <div class="section-title">Debit Note Details</div>
    <table class="border deduction-table" style="margin-bottom:3px;">
      <thead>
        <tr>
          <th style="width:8%;">Sr</th>
          <th style="width:52%;">Particulars</th>
          <th style="width:20%;">Weight (Kg)</th>
          <th style="width:20%;">Amount</th>
        </tr>
      </thead>
      <tbody>
        {% for row in doc.debit_note %}
        {% set weight = frappe.utils.flt(row.deducted_weight_kg or 0) %}
        {% set amt = frappe.utils.flt(row.amount or 0) %}
        <tr>
          <td class="center nowrap">{{ loop.index }}</td>
          <td class="left" style="padding-left:4px;">{{ row.particulars or '' }}</td>
          <td class="right nowrap">{{ '%.2f' % weight }}</td>
          <td class="right nowrap">{{ '%.2f' % amt }}</td>
        </tr>
        {% endfor %}
        {% set total_debit_amount = doc.debit_note|sum(attribute='amount') or 0 %}
        <tr class="highlight">
          <td colspan="3" class="right"><b>Total Debit Amount</b></td>
          <td class="right nowrap"><b>{{ '%.2f' % total_debit_amount }}</b></td>
        </tr>
      </tbody>
    </table>

    <!-- DEBIT NOTE SUMMARY - below debit note details -->
    {% set app_settings = frappe.get_doc('App Settings', 'App Settings') %}
    {% set basic_value_net_tds = doc.debit_note|sum(attribute='amount') or 0 %}
    {% set cgst_amount = frappe.utils.flt(doc.cgst_amount or 0) %}
    {% set sgst_amount = frappe.utils.flt(doc.sgst_amount or 0) %}
    {% set igst_amount = frappe.utils.flt(doc.igst_amount or 0) %}
    {% set cgst_rate = frappe.utils.flt(app_settings.debit_note_cgst_rate or 2.5) %}
    {% set sgst_rate = frappe.utils.flt(app_settings.debit_note_sgst_rate or 2.5) %}
    {% set igst_rate = frappe.utils.flt(app_settings.debit_note_igst_rate or 5.0) %}

    {% set gst_amount = 0 %}
    {% if (cgst_amount > 0) or (sgst_amount > 0) %}
      {% set gst_amount = basic_value_net_tds * (cgst_rate + sgst_rate) / 100 %}
    {% elif (cgst_amount == 0) and (sgst_amount == 0) and (igst_amount > 0) %}
      {% set gst_amount = basic_value_net_tds * igst_rate / 100 %}
    {% endif %}

    {% set net_payable = basic_value_net_tds + gst_amount %}

    <div class="section-title" style="margin-top:0px;">Debit Note Summary</div>
    <table class="border">
      <tr>
        <td class="left" style="width:60%;"><b>Document</b></td>
        <td class="right nowrap" style="width:40%;">Debit Note</td>
      </tr>
      <tr>
        <td class="left"><b>Basic Value Net of TDS</b></td>
        <td class="right nowrap">{{ '%.2f' % basic_value_net_tds }}</td>
      </tr>
      <tr>
        <td class="left"><b>GST Amount</b></td>
        <td class="right nowrap">{{ '%.2f' % gst_amount }}</td>
      </tr>
      <tr class="highlight">
        <td class="left"><b>Net Payable</b></td>
        <td class="right nowrap"><b>{{ '%.2f' % net_payable }}</b></td>
      </tr>
    </table>
  </div>
  {% endif %}

  <!-- GST SUMMARY - right side of debit note details -->
  {% if doc.debit_note and doc.debit_note|length > 0 %}
  <div style="width:38%; float:right; margin-top:2px;">
  {% else %}
  <div style="width:46%; float:right; margin-top:2px;">
  {% endif %}
    <div class="section-title">GST Summary</div>
    <table class="border">
      <tr>
        <td class="left" style="width:60%;">CGST ({{ doc.cgst_percent or 0 }}%)</td>
        <td class="right nowrap" style="width:40%;">{{ '%.2f' % (frappe.utils.flt(doc.cgst_amount or 0)) }}</td>
      </tr>
      <tr>
        <td class="left">SGST ({{ doc.sgst_percent or 0 }}%)</td>
        <td class="right nowrap">{{ '%.2f' % (frappe.utils.flt(doc.sgst_amount or 0)) }}</td>
      </tr>
      <tr>
        <td class="left">IGST ({{ doc.igst_percent or 0 }}%)</td>
        <td class="right nowrap">{{ '%.2f' % (frappe.utils.flt(doc.igst_amount or 0)) }}</td>
      </tr>
      <tr class="highlight">
        <td class="left"><b>Net Total</b></td>
        <td class="right nowrap"><b>{{ '%.2f' % (frappe.utils.flt(doc.net_total or 0)) }}</b></td>
      </tr>
    </table>
  </div>
  <div style="clear:both;"></div>

  <!-- Footer (pinned to bottom) -->
  <div class="footer">
    <table class="no-border" style="width:100%;">
      <tr>
        <td style="width:65%; vertical-align:top; font-size:7px;">
          <i>This is a computer-generated settlement advice and does not require a signature.</i><br>
          <b>Printed By:</b> {{ frappe.session.user }}<br>
          <b>Printed On:</b> {{ frappe.utils.format_datetime(frappe.utils.now_datetime()) }}
        </td>
        <td style="width:35%; vertical-align:bottom; text-align:center;">
          <div style="height:30px; border-bottom:1px solid #000; margin:0 15px;"></div>
          <div style="margin-top:1.5px; font-size:7px;"><b>Authorized Signatory</b></div>
        </td>
      </tr>
    </table>
  </div>
</div>
